function createOptionArr(selectId, arr){
  var empty_op;
  for(i=0; i<arr.length; i++){
    empty_op = document.createElement("OPTION");
    empty_op.value = arr[i];
    empty_op.text = arr[i];
    document.getElementById(selectId).appendChild(empty_op);
  }
}

function createOption(selectId, value){
  var empty_op;
  empty_op = document.createElement("OPTION");
  empty_op.value = value;
  empty_op.text = value;
  document.getElementById(selectId).appendChild(empty_op);
}

function updateTypes(){
  $('#select_article_type').empty();
  createOptionArr("select_article_type", basicOptions);
  $('#select_article_name').empty();
  createOptionArr("select_article_name", basicOptions);
  var Author_name = $('#select_authors').find(":selected").text();
  var types = {};
  if(Author_name!= "הכל"){
    var indexOfAuthor = myData.findIndex(i => i.Author_name === Author_name);
    for (var j=0 ; j<myData[indexOfAuthor].Types.length; j++){
        if(myData[indexOfAuthor].Types[j].Type==" ")
          myData[indexOfAuthor].Types[j].Type="שונות";
        types[myData[indexOfAuthor].Types[j].Type] = myData[indexOfAuthor].Types[j].Type;
    }
    createOptionArr("select_article_type", Object.keys(types));
  }else{
    for(var i=0; i<myData.length; i++){
      for (var j=0 ; j<myData[i].Types.length; j++){
        if(myData[i].Types[j].Type==" ")
          myData[i].Types[j].Type="שונות";
        types[myData[i].Types[j].Type] = myData[i].Types[j].Type;
      }
    }
    console.log("types: ", types);
    createOptionArr("select_article_type", Object.keys(types));
  }
}

